import React, { Component } from "react";
import SingleNews from "./SingleNews";
class News extends Component {
  state = {
    news: [],
  };
  componentDidMount() {
    const url =
      "http://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=cfbd452f77d34140abebe073a718a047";
    fetch(url)
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        this.setState({
          news: data.articles,
        });
      })
      .catch((error) => console.log("Error", error));
  }

  RenderItems = () => {
    return this.state.news.map((item) => <SingleNews item={item} />);
  };

  render() {
    return <ul>{this.RenderItems()}</ul>;
  }
}

export default News;
