import React, { Component } from "react";
import axios from "axios";
import SingleSideNews from "./SingleSideNews";
class SideNews extends Component {
  //received news props
  state = {
    sidenews: [],
    error: false,
  };
  componentDidMount() {
    const url = `http://newsapi.org/v2/${this.props.news.type}?${this.props.news.source}&apiKey=cfbd452f77d34140abebe073a718a047`;
    axios
      .get(url)
      .then((response) => {
        this.setState({
          sidenews: response.data.articles,
        });
      })
      .catch((error) => console.log("Error:", error));
  }

  RenderItems = () => {
    return this.state.sidenews.map((item) => (
      <SingleSideNews item={item} key={item.url} />
    ));
  };

  render() {
    return <ul className="collection">{this.RenderItems()}</ul>;
  }
}

export default SideNews;
