import React, { Component } from "react";
import SingleSideNews from "./SingleSideNews";
class SideNews extends Component {
  state = {
    news: [],
  };
  componentDidMount() {
    const url = `http://newsapi.org/v2/${this.props.news.type}?${this.props.news.source}&apiKey=cfbd452f77d34140abebe073a718a047`;
    fetch(url)
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        this.setState({
          news: data.articles,
        });
      })
      .catch((error) => console.log("Error", error));
  }

  RenderItems = () => {
    return this.state.news.map((item) => (
      <SingleSideNews item={item} key={item.url} />
    ));
  };

  render() {
    return (
      <div className="container ui">
        <div className="ui divided items">{this.RenderItems()}</div>
      </div>
    );
  }
}

export default SideNews;
