import React, { Component } from "react";
import News from "../components/News/News";
import SideNews from "../components/News/SideNews";

class App extends Component {
  state = {
    news1: {
      type: "everything",
      source: "domains=wsj.com&language=en",
    },
    news2: {
      type: "top-headlines",
      source: "sources=bbc-news",
    },
    news3: {
      type: "everything",
      source: "domains=comicbookmovie.com&language=en",
    },
  };
  render() {
    return (
      <div className="container-fluid">
        <div className="navbar-fixed">
          <nav>
            <div className="nav-wrapper red lighten-1">
              <a href="http://localhost:3000" className="bran-logo center">
                My Feed
              </a>
            </div>
          </nav>
        </div>
        <div className="row">
          <div className="col s8">
            <h4>Latest News</h4>
            <News news={this.state.news2} />
            <News news={this.state.news1} />
          </div>
          <div className="col s4">
            <h4>More News</h4>
            <SideNews />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
